<?php	class EventWidget extends CWidget	{		public $title = array();		public $event = array();				protected function registerClientScript()		{			// ...publish CSS or JavaScript file here...			/*$cs=Yii::app()->clientScript;			$cs->registerCssFile($cssFile);			$cs->registerScriptFile($jsFile);*/		
}				public function run() {									$criteria = new CDbCriteria;			$criteria->condition = 'content_type_id = 4 AND publish = 1';			$criteria->order = 'update_time ASC';			$event = Content::model()->findAll($criteria);						$vars= array(				'title'=> $this->title,				'event' => $event							);						$this->render('eventwidget', $vars);		}			}?>